package java2;

public class JavaEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int num1=30;
   int num2=20;
   int sum=num1+num2;
   int sub=num1-num2;
   int multi=num1*num2;
   int divi=num1/num2;
   System.out.println("The sum of two numbers = "+sum);
   System.out.println("The sub of two numbers = "+sub);
   System.out.println("The multi of two numbers = "+ multi);
   System.out.println("The division of two numbers = "+ divi);
	}

}
